import React, { useState, useRef } from 'react';
import Wrapper from '../Helpers/Wrapper';

import Button from '../UI/Button';
import Card from '../UI/Card';
import ErrorModal from '../UI/ErrorModal';
import classes from './AddUser.module.css';

const AddUser = props => {
    const nameInputRef = useRef();
    const ageInputRef = useRef();

    const [enteredUsername, setEnteredUsername] = useState('');
    const [enteredAge, setEnteredAge] = useState('');
    const [errorState, setErrorState] = useState();


    const addUserHandler = (event) => {
        event.preventDefault();
        const enteredName = nameInputRef.current.value;
        const enteredAge = ageInputRef.current.value;
        if(enteredUsername.trim() === 0 || enteredAge.trim().length === 0) {
            setErrorState(true);
            return;
        }
        if(+enteredAge < 1) {
                setErrorState(true);
            return;
        }
        props.onAddUser(enteredUsername, enteredAge);
        setEnteredUsername('');
        setEnteredAge('');
    }

    const usernameChangeHandler = (event) => {
        setEnteredUsername(event.target.value);
        console.log(event.target.value)
    }

    const ageChangeHandler = (event) => {
        setEnteredAge(event.target.value);
        console.log(event.target.value);
    }

    const errorHandler = () => {
        setErrorState(null);
    }

    return (
        <Wrapper>
            {errorState && <ErrorModal title="An error occured!" message="Something went wrong!" onConfirm={errorHandler}/>}
            <Card className={classes.input}>
                <form onSubmit={addUserHandler}>
                    <label htmlFor="username" >Username</label>
                    <input onChange={usernameChangeHandler} value={enteredUsername} id="username" type="text" ref={nameInputRef} />
                    <label htmlFor="age">Age (Years)</label>
                    <input onChange={ageChangeHandler} value={enteredAge} id="age" type="text" ref={ageInputRef}/>
                    <Button type="submit">Add User</Button>
                </form>
            </Card>
        </Wrapper>
    );
};

export default AddUser;
